const Answer = ({answerText, onSelectAnswer, index, currentAnswer, correctAnswer}) => {
    const letterMapping = ['A', 'B', 'C', 'D'];
    const isCorrectAnswer = currentAnswer && answerText === correctAnswer;
    const isWrongAnswer = answerText != correctAnswer && currentAnswer === answerText;
    const correctAnswerClass =  isCorrectAnswer ? "correct-answer" : "";
    const wrongAnswerClass = isWrongAnswer ? "wrong-answer" : "";
    const disabledClass = currentAnswer ? "disabled-answer" : "";

    return (
    <div className={`answer ${correctAnswerClass} ${wrongAnswerClass} ${disabledAnswerClass}`} 
    onClick={() => onSelectAnswer(answerText)}>
        <div className="answer-letter">{letterMapping[index]}</div>
        <div className="answer-text">{answerText}</div>
    </div>
    )
};

export default Answer;
